const item = $input.first();
const data = item.json.body || item.json;
const mockText = data.mockText;

if (!mockText) {
  throw new Error('No mockText provided. Received: ' + JSON.stringify(data));
}

const lines = mockText.split('\n').filter(l => l.trim());
let workoutName = 'Imported Workout';
const exercises = [];

for (const line of lines) {
  // Try to parse exercise line: "1. Bench Press - 4x10 @ 60kg" or "Bench Press - 3x12 @ 40kg"
  const exerciseMatch = line.match(/^\d*\.?\s*(.+?)\s*-\s*(\d+)x(\d+)\s*@\s*([\d.]+)\s*kg/i);
  if (exerciseMatch) {
    const [, name, sets, reps, weight] = exerciseMatch;
    const setsArray = [];
    for (let i = 0; i < parseInt(sets); i++) {
      setsArray.push({
        type: 'normal',
        weight_kg: parseFloat(weight),
        reps: parseInt(reps),
        distance_meters: null,
        duration_seconds: null
      });
    }
    exercises.push({
      exercise_template_id: 'D04AC939',
      superset_id: null,
      rest_seconds: 90,
      notes: name.trim(),
      sets: setsArray
    });
  } else if (!line.match(/^\d+\./) && !exercises.length) {
    // First non-exercise line is the workout name
    workoutName = line.trim();
  }
}

// If no exercises found, create a default one
if (exercises.length === 0) {
  exercises.push({
    exercise_template_id: 'D04AC939',
    superset_id: null,
    rest_seconds: 90,
    notes: 'Default exercise',
    sets: [{ type: 'normal', weight_kg: 20, reps: 10, distance_meters: null, duration_seconds: null }]
  });
}

return [{
  json: {
    routine: {
      title: workoutName,
      folder_id: null,
      exercises: exercises
    }
  }
}];
